export function hydrateProperties(
  domElement: Element,
  tag: string,
  props:  Object,
  hostContext: HostContext,
): boolean {
  if (__DEV__) {
    validatePropertiesInDevelopment(tag, props);
  }

  switch (tag) {
    case 'dialog': 
      listenToNonDelegatedEvent('cancel', domElement);
      listenToNonDelegatedEvent('close', domElement);
      break;
    case 'iframe':
    case 'object':
    case 'embed':
      listenToNonDelegatedEvent('load', domElement);
      break;
    case 'video': 
    case 'audio': 
      for (let i = 0; i < mediaEventTypes.length; i++) {
        listenToNonDelegatedEvent(mediaEventTypes[i], domElement);
      }
      break;
    case 'source': 
      listenToNonDelegatedEvent('error', domElement);
      break;
    case 'img':
    case 'image':
    case 'link':
      listenToNonDelegatedEvent('error', domElement);
      listenToNonDelegatedEvent('load', domElement);
      break;
    case 'details':
      listenToNonDelegatedEvent('toggle', domElement);
      break;
    case 'input':
      if (__DEV__) {
        checkControlledValueProps('input', props);
      }
      listenToNonDelegatedEvent('invalid', domElement);
      validateInputProps(domElement, props);
      if (!enableHydrationChangeEvent) {
        initInput(
          domElement,
          props. value,
          props.defaultValue,
          props.checked,
          props.defaultChecked,
          props.type,
          props.name,
          true,
        );
      }
      break;
    case 'option': 
      validateOptionProps(domElement, props);
      break;
    case 'select':
      if (__DEV__) {
        checkControlledValueProps('select', props);
      }
      listenToNonDelegatedEvent('invalid', domElement);
      validateSelectProps(domElement, props);
      break;
    case 'textarea':
      if (__DEV__) {
        checkControlledValueProps('textarea', props);
      }
      listenToNonDelegatedEvent('invalid', domElement);
      validateTextareaProps(domElement, props);
      if (!enableHydrationChangeEvent) {
        initTextarea(
          domElement,
          props. value,
          props.defaultValue,
          props.children,
        );
      }
      break;
  }

  // Custom elements need their props (including event handlers) re-applied
  // during hydration because the server markup cannot capture property-based
  // listeners.  Mirror the client mount path used in setInitialProperties.
  if (isCustomElement(tag, props)) {
    for (const propKey in props) {
      if (!props.hasOwnProperty(propKey)) {
        continue;
      }
      const propValue = props[propKey];
      if (propValue === undefined) {
        continue;
      }
      setPropOnCustomElement(
        domElement,
        tag,
        propKey,
        propValue,
        props,
        undefined,
      );
    }
    return true;
  }

  const children = props.children;
  if (
    typeof children === 'string' ||
    typeof children === 'number' ||
    typeof children === 'bigint'
  ) {
    if (
      domElement.textContent !== '' + children &&
      props.suppressHydrationWarning !== true &&
      !checkForUnmatchedText(domElement.textContent, children)
    ) {
      return false;
    }
  }

  if (props.popover != null) {
    listenToNonDelegatedEvent('beforetoggle', domElement);
    listenToNonDelegatedEvent('toggle', domElement);
  }

  if (props.onScroll != null) {
    listenToNonDelegatedEvent('scroll', domElement);
  }

  if (props. onScrollEnd != null) {
    listenToNonDelegatedEvent('scrollend', domElement);
    if (enableScrollEndPolyfill) {
      listenToNonDelegatedEvent('scroll', domElement);
    }
  }

  if (props.onClick != null) {
    trapClickOnNonInteractiveElement(((domElement: any): HTMLElement));
  }

  return true;
}
